int num[25];
bool vis[25];
int n,k,ans;
void dfs(int x,int sum,int y){
	if(x == k)
		if(factorial[sum]){
			ans ++;
			return;
		}
	for(int i=y ; i<n ; i++)
		if(!vis[i]){
			vis[i] = true;
			dfs(x + 1 , sum + num[i] , i+1);
			vis[i] = false;
		}
}
int main()
{
	scanf("%d%d",&n,&k);
	memset(vis,false,sizeof vis);
	ans = 0;
	for(int i=0 ; i<n ; i++)
		scanf("%d",num+i);
	dfs(0,0,0);
	printf("%d",ans);
	return 0;
}
